//import images
import logo from './images/LogoHortus.png'

//import stylesheets
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

// import pre-made components
import {Helmet} from 'react-helmet';
import {Row, Col} from 'react-bootstrap';

// import icons
import { faFacebook } from "@fortawesome/free-brands-svg-icons";
import { faInstagram } from "@fortawesome/free-brands-svg-icons";
import { faTwitter } from "@fortawesome/free-brands-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

// import self-made components
import Content from './components/Content';

function App() {
	let startScreen;


	function startHandler(){
		console.log("startHandler");
		startScreen = <Content/>
	}

	return (
	<div className="App">
		{/*Navigation bar title*/}
		<Helmet>
			<title> Stoepplantjes </title>
		</Helmet>

		{/*Header*/}
		<div className="header">
			<Row>
				<Col className="logo" md={2} lg={2}>
						<img src={logo} alt="Hortus Botanicus" width="100%"/>
				</Col>
				<Col className ="title" md={10} lg={8}>
						<h1> Welk stoepplantje heb ik gevonden? </h1>
				</Col>
				<Col md={3} lg={2}></Col>
			</Row>
		</div>

		{/*Content*/}
		{console.log("to the Content")}
		<p> Hoe vaak lopen we niet achteloos langs stoepplantjes, we zien ze niet eens. Maar ze zijn ook bewoners van de stad of het dorp waar we wonen. Ze zijn al heel lang onder ons en hoewel ze een onopvallend bestaan leiden - het zijn taaie rakkers. En ze zijn belangrijk!

		Bla bla bla meer intro.... Misschien aan het begin melden dat het maar om 52 plantjes gaat in het kader van meer dan 800 in onze steden. beginscherm
		</p>
		<button className="fa JN" onClick={startHandler}> START </button>

		<Content/>

		{/*Footer*/}
		<div className="fixed-bottom">
			<div className="footer">
				<FontAwesomeIcon className ="iconF" icon={faFacebook}/>
				<FontAwesomeIcon className ="iconF" icon={faInstagram} />
				<FontAwesomeIcon className="iconF" icon={faTwitter} />
			</div>
		</div>
	</div>
	);
}

export default App;
